float sigmoid(float x) {
    return 1 / (1 + exp(-x));
}

float neuron(float W[], float B, float X[], int n) {
    float z, y = 0.0;
    for (int i = 0; i < n; i++) {
        y += W[i] * X[i];
    }
    y += B;
    z = sigmoid(y);
    return z;
}

float loss_crossentropy(float ytrue[], float ypred[]) {
    float loss = 0;
    for (int i = 0; i < samples; i++) {
        loss += -ytrue[i] * log(ypred[i]) - (1 - ytrue[i]) * log(1 - ypred[i]);
    }
    loss /= samples;
    return loss;
}

void get_gradient(float ytrue[], float ypred[]) {
    db = 0;
    for (int i = 0; i < samples; i++) {
        db += (ypred[i] - ytrue[i]);
        int jj = n_features * i;
        for (int k = 0; k < n_features; k++) {
            dw[k] += X[jj + k] * (ypred[i] - ytrue[i]);
        }
    }
    db /= samples;
    for (int k = 0; k < n_features; k++) {
        dw[k] /= samples;
    }
}

